3.times do |i|
    image = Image.create(name: "Fry", url: "https://i.pinimg.com/564x/64/a9/30/64a93049ac617f6b8ff20724babdbe08.jpg")
    image.annotations.create(visual: "data:image/png;base64,", written: "Whimmy-wham-wham-wazzle!")
    image.annotations.create(visual: "data:image/png;base64,", written: "Outside Slurm Factory. The ship descends from the clouds, the landing gear comes down and it lands outside the Slurm factory which bears an uncanny resemblance to the Wonka factory in Willy Wonka And The Chocolate Factory. A banner reading \"Welcome Contest Winners!\" has been hung above the gates. Fry, Bender, Leela, Amy and the rest of the staff walk out of the ship down the steps.")
    image.annotations.create(visual: "data:image/png;base64,", written: "Welcome, my friends, to the wondrous world of whimsy that we like to call \"Slurms Centralised Industrial Fabrication Unit\".")
end

